 = ( get_query_var('paged') ) ? get_query_var('paged') : 1; ?>                                                <?php else : ?>                                                <?php                                                     $revistas = new WP_Query( array(                                                            'post_type' => 'dflip',                                                            'posts_per_page' => 12,                                                            'paged' => $paged                                                    ));                                                ?>                                                    <?php if ( $revistas->have_posts() ) : $i = 0; ?>                                                        <div class="row">                                                        <?php while( $revistas->have_posts() ) : $revistas->the_post(); $i++; ?>                                                                <?php                                                                 $dflip_data = get_post_meta( $post->ID, '_dflip_data' );                                                                $thumbnail = $dflip_data[0][pdf_thumb];                                                                $link = add_query_arg( array('ed' =>  $post->ID), home_url( add_query_arg(array(), $wp->request) ) );                                                                ?>                                                                                                                                <div class="col-sm-3">                                                                    <?php if ( has_the_subscription( $current_user->ID, strtotime( date('j-n-Y') ) ) ) : ?>                                                                    <a href="<?php echo $link ?>" >                                                                    <?php endif; ?>                                                                        <div class="img-container">                                                                            <?php if ( $thumbnail != NULL ) : ?>                                                                                <div class="image" style="background-image: url(<?php echo $thumbnail ?>)"></div>                                                                            <?php endif; ?>                                                                        </div>                                                                        <p class="titulo_revista"><?php the_title() ?></p>                                                                    <?php if ( has_the_subscription( $current_user->ID, strtotime( date('j-n-Y') ) ) ) : ?>                                                                    </a>                                                                    <?php endif; ?>                                                                </div>                                                                                                                            <?php if ( $i % 4 == 0 ) : ?>                                                                    </div>                                                                    <div class="row">                                                            <?php endif; ?>                                                        <?php endwhile; ?>                                                        </div>                                                <?php endif; ?>                                                <?php endif; ?>                                        </div><!-- .entry-content -->                                            <?php                                              if (function_exists(ed_custom_pagination)) {                                                ed_custom_pagination($revistas->max_num_pages,"",$paged);                         